1 2 3 4 5 6 7 8 9  #include&lt;iostream&gt;using namespace std; long long d[10005] = { 1,1 }, n, m, i; int main() { cin &gt;&gt; n &gt;&gt; m; for (i = 2; i &lt;= n; i++) d[i] = (d[i - 1] + (i - m &gt;= 0 ? d[i - m] : 0)) % 1000000007; cout &lt;&lt; d[n] &lt;&lt; endl; }
